      <ng-container *ngFor="let allEmps of AllEmployee">
        <tr>
          <td>{{ allEmps.id }}</td>
          <td>{{ allEmps.name }}</td>
          <td>{{ allEmps.age }}</td>
          <td>{{ allEmps.job }}</td>
