import { Component } from '@angular/core';
import { IEmployee } from 'src/Shared/Interfaces/IEmployee';
import { EmployeeServiceService } from 'src/Shared/Services/employee-service.service';
import { HubConnectionBuilder } from '@microsoft/signalr';
import { ToastrService } from 'ngx-toastr';
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {
  private hubConnection: any;
  title = 'Corelia';
  AllEmployee: IEmployee[] = [];
  constructor(
    private employeeServiceService: EmployeeServiceService,
    private toastr: ToastrService
  ) {}
  ngOnInit() {
    this.employeeServiceService.GetAll().subscribe((data: any) => {
      this.AllEmployee = data;
      console.log(this.AllEmployee);
    });
    this.initializeSignalR();
  }
  private initializeSignalR() {
    this.hubConnection = new HubConnectionBuilder()
      .withUrl('https://localhost:7160/notificationHub') // Replace with your SignalR hub URL
      .build();

    this.hubConnection.start().then(() => {
      console.log('Hub connection started');

      // Subscribe to the 'ReceiveNotification' event
      this.hubConnection.on('ReceiveNotification', (message: string) => {
        // Handle the received notification (e.g., update the employee list)
        console.log('Received Notification:', message);
        this.refreshEmployeeList();
        this.showNotification(message);
      });
    });
  }
  private refreshEmployeeList() {
    this.employeeServiceService.GetAll().subscribe((data: any) => {
      this.AllEmployee = data;
    });
  }
  private showNotification(message: string) {
    this.toastr.success(message, 'Notification', {
      timeOut: 3000, // Notification duration in milliseconds
      progressBar: true,
      positionClass: 'toast-top-right', // Toast position
    });
  }
}
<nav class="navbar navbar-expand-lg navbar-light bg-light">
  <a class="navbar-brand" href="#">Corelia</a>
  <button
    class="navbar-toggler"
    type="button"
    data-toggle="collapse"
    data-target="#navbarNavAltMarkup"
    aria-controls="navbarNavAltMarkup"
    aria-expanded="false"
    aria-label="Toggle navigation"
  >
    <span class="navbar-toggler-icon"></span>
  </button>
  <div class="collapse navbar-collapse" id="navbarNavAltMarkup">
    <div class="navbar-nav">
      <a class="nav-item nav-link active" href="#"
        >Home <span class="sr-only">(current)</span></a
      >
      <a class="nav-item nav-link" href="#">Features</a>
      <a class="nav-item nav-link" href="#">Pricing</a>
      <a class="nav-item nav-link disabled" href="#">Disabled</a>
    </div>
  </div>
</nav>
<div class="container">
  <table class="table">
    <thead class="thead-light">
      <tr>
        <th scope="col">Id</th>
        <th scope="col">Name</th>
        <th scope="col">Age</th>
        <th scope="col">Job</th>
      </tr>
    </thead>
    <tbody>
      <ng-container *ngFor="let allEmps of AllEmployee">
        <tr>
          <td>{{ allEmps.id }}</td>
          <td>{{ allEmps.name }}</td>
          <td>{{ allEmps.age }}</td>
          <td>{{ allEmps.job }}</td>
        </tr>
      </ng-container>
    </tbody>
  </table>
</div>
